"use strict";const button=document.querySelector(".js-button"),movieTitle=document.querySelector(".js-title"),movieImg=document.querySelector(".js-img"),movieYear=document.querySelector(".js-year"),moviePlot=document.querySelector(".js-plot"),movieDirector=document.querySelector(".js-director"),movieWriter=document.querySelector(".js-writer"),movieActors=document.querySelector(".js-actors"),genreList=document.querySelector(".js-genre-list"),movieScore=document.querySelector(".js-score"),result=document.querySelector(".js-result"),movieId=document.querySelector(".js-imdb"),apiKey="96fab62e";function getRandomMovieId(){var t=imdbIdArray[Math.floor(Math.random()*imdbIdArray.length)];return console.log(`IMDB ID: ${t}`),t}async function fetchMovieData(t){const e=`https://www.omdbapi.com/?i=${t}&apikey=96fab62e`,o=await fetch(`${e}`),r=await o.json();movieTitle.textContent=r.Title,movieImg.src=r.Poster,movieYear.textContent=r.Year,moviePlot.textContent=r.Plot,movieDirector.textContent=r.Director,movieWriter.textContent=r.Writer,movieActors.textContent=r.Actors,movieScore.textContent=r.Metascore,movieId.href=`https://www.imdb.com/title/${r.imdbID}`;r.Genre.split(", ").forEach((t=>{const e=document.createElement("li");e.textContent=t,genreList.appendChild(e)}));const n=r.Metascore,c=parseInt(n);switch(!0){case c>=85:result.textContent=" - Excellent";break;case c>=70:result.textContent=" - Very Good";break;case c>=50:result.textContent=" - Good";break;case c<50:result.textContent=" - Underwhelming"}}window.addEventListener("DOMContentLoaded",(()=>{console.log("DOM fully loaded and parsed"),button.addEventListener("click",fetchMovieData(getRandomMovieId()))}));const imdbIdArray=["tt9114286","tt14641788","tt17076046","tt13833688","tt10731256","tt1016150","tt15791034","tt6443346","tt10403420","tt7846844","tt1630029","tt13139228","tt13640696","tt15474916","tt4273800","tt11564570","tt11813216","tt12593682","tt14715170","tt1745960"];